(267, 23, 'de', 'Create Contract', '<p>&nbsp;</p>\n                    <p><b>Hi</b> {contract_client}</p>\n                    <p>&nbsp;<b style=\"font-family: var(--bs-body-font-family); text-align: var(--bs-body-text-align);\">Vertragsgegenstand :</b><span style=\"font-family: var(--bs-body-font-family); font-weight: var(--bs-body-font-weight); text-align: var(--bs-body-text-align);\"> {contract_subject}</span></p>\n                    <p><b>Vertragsprojekt :&nbsp;</b>{contract_project}</p>\n                    <p><b>Startdatum&nbsp;</b>: {contract_start_date}</p>\n                    <p><b>Enddatum&nbsp;</b>: {contract_end_date}</p>\n                    <p>Freuen Sie sich auf das H철ren von Ihnen.</p>\n                    <p><b>G체tige Gr체e,</b></p>\n                    <p>{company_name}</p>', '2022-10-26 17:37:58', '2022-10-26 17:37:58'),
