(276, 23, 'pt', 'Create Contract', '<p>&nbsp;</p>\n                    <p><span style=\"text-align: var(--bs-body-text-align);\"><b>Ol찼</b></span>&nbsp;{contract_client}</p>\n                    <p><span style=\"text-align: var(--bs-body-text-align);\"><b>Assunto do Contrato</b></span>&nbsp;: {contract_subject}</p>\n                    <p><span style=\"text-align: var(--bs-body-text-align);\"><b>Projeto de contrato&nbsp;</b></span>: {contract_project}</p>\n                    <p><span style=\"text-align: var(--bs-body-text-align);\"><b>Data de in챠cio</b></span><b>&nbsp;</b>: {contract_start_date}</p>\n                    <p><span style=\"text-align: var(--bs-body-text-align);\"><b>Data final</b></span><b>&nbsp;</b>: {contract_end_date}</p>\n                    <p>Ansioso para ouvir de voc챗.</p>\n                    <p><b>Atenciosamente,</b><br></p>\n                    <p>{company_name}</p>', '2022-10-26 17:37:58', '2022-10-26 17:37:58');
